package com.sorbac.codeChef.sept14.cheflr;

import org.testng.annotations.Test;

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

public class MainTest {

	@Test
	public void test() throws Exception {
		assertThat(Main.getPosition("lrl"), is(10l));
		assertThat(Main.getPosition("rll"), is(14l));
		assertThat(Main.getPosition("r"), is(4l));
		assertThat(Main.getPosition("lllr"), is(13l));
		assertThat(Main.getPosition("l"), is(2l));
		assertThat(Main.getPosition("lrll"), is(19l));
		assertThat(Main.getPosition("llr"), is(8l));
		assertThat(Main.getPosition("rrll"), is(35l));
		assertThat(Main.getPosition("rlll"), is(27l));
	}

	@Test
	public void testInfinity() throws Exception {
		List<String> myLevel = new ArrayList<String>();
		myLevel.add("");
		long myEven = 0;
		long myOdd = 1;
		boolean isEven = true;
		while (true) {
			long myValue = isEven ? myEven : myOdd;
			List<String> myNewLevel = new ArrayList<String>();
			for (String myString : myLevel) {
				myValue = (myValue + 2) % 1000000007;
				System.out.print(myString + "l" + Main.getPosition(myString + "l") + " ");
				//assertThat("Reason : " + myString + "l",Main.getPosition(myString + "l"), is(myValue));
				if (Main.getPosition(myString + "l") != myValue) {
					System.out.println("Error! : " + myString + "l");
				}
				;
				myValue = (myValue + 2) % 1000000007;
				System.out.print(myString + "r" + Main.getPosition(myString + "r") + " ");
				//System.out.print(myValue + " ");
				if (Main.getPosition(myString + "r") != myValue) {
					System.out.println("Error! : " + myString + "r");
				}
				;
				myNewLevel.add(myString + "l");
				myNewLevel.add(myString + "r");
			}
			//System.out.println();
			if (isEven) {
				myEven = myValue;
			} else {
				myOdd = myValue;
			}
			myLevel = myNewLevel;
			isEven = !isEven;
			System.out.println(binlog(myLevel.size()));
		}
	}

	@Test
	public void testOverlap() throws Exception {
		String myLeftOdd = "";
		//long myValueOdd = 4*(((long)Math.pow(2, myLeftOdd.length() - 1) - 1)/ 3) + 2;
		BigDecimal myBigDecimal2 = new BigDecimal(2);
		long myValueOdd = myBigDecimal2.pow(myLeftOdd.length() - 1).subtract(BigDecimal.ONE).divide(
				new BigDecimal(3)).multiply(new BigDecimal(4)).add(new BigDecimal(2)).remainder(
				new BigDecimal(1000000007)).intValue();
		//myValueOdd = myValueOdd % 1000000007;
		assertThat(Main.getPosition(myLeftOdd), is(myValueOdd));
		String myLeftEven = "";
		long myValueEven = new BigDecimal(2).pow(myLeftEven.length()).subtract(BigDecimal.ONE).divide(
				new BigDecimal(3)).multiply(new BigDecimal(2)).add(BigDecimal.ONE).remainder(
				new BigDecimal(1000000007)).intValue(); //2*(((long)Math.pow(2, myLeftEven.length() ) - 1)/ 3) + 1;
		//myValueEven = myValueEven % 1000000007;
		assertThat(Main.getPosition(myLeftEven), is(myValueEven));
	}

	public static int binlog(int bits) // returns 0 for bits=0
	{
		int log = 0;
		if ((bits & 0xffff0000) != 0) {
			bits >>>= 16;
			log = 16;
		}
		if (bits >= 256) {
			bits >>>= 8;
			log += 8;
		}
		if (bits >= 16) {
			bits >>>= 4;
			log += 4;
		}
		if (bits >= 4) {
			bits >>>= 2;
			log += 2;
		}
		return log + (bits >>> 1);
	}
}